local fireModel = 2023function applyFire()    local fire = engineLoadDFF("fire.dff",2023)    engineReplaceModel(fire,fireModel)endaddEventHandler("onClientResourceStart",resourceRoot,applyFire)function createExtinguisher(wep,_,_,hitX,hitY,hitZ)    if wep == 42 and math.random(1,10)==1 then    for k, v in ipairs(getElementsByType("object",resourceRoot)) do        if getElementModel(v) == fireModel then            local fX,fY,fZ = getElementPosition(v)            local dist = getDistanceBetweenPoints2D(hitX,hitY,fX,fY)            if dist < 2.1 then                triggerServerEvent("fireExtinguished",localPlayer,v)            end        end    end    end    -- if wep == 37 and math.random(1,5)==1 then    -- tworzymy ogien!      -- triggerServerEvent("doCreateFire", root, hitX, hitY, hitZ, getElementDimension(localPlayer), getElementInterior(localPlayer))    -- endendaddEventHandler("onClientPlayerWeaponFire",localPlayer,createExtinguisher)addEventHandler("onClientRender", root, function()local rootx,rooty,rootz=getCameraMatrix()for k,col in ipairs(getElementsByType("object",resourceRoot)) do    if getElementModel(col) == fireModel then	if not getElementData(col,"fire:Endvalue") then return end	local x,y,z=getElementPosition(col)	local sx,sy =getScreenFromWorldPosition(x, y, z+1)	if sx and sy then		local distance = getDistanceBetweenPoints3D(rootx,rooty,rootz,x,y,z)		if distance <= 6 then			dxDrawText (getElementData(col,"fire:Endvalue").."%", sx, sy, sx, sy, tocolor ( 255, 255, 255, 150 ), 2, "defalut-bold", "center","center")		end	endendendend)